import React, { useState } from 'react';

const PulihkanAkun = () => {
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [passwordVisible, setPasswordVisible] = useState(false);

  const togglePasswordVisibility = () => {
    setPasswordVisible(!passwordVisible);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (!password || !confirmPassword) {
      alert('Please fill in all fields.');
      return;
    }

    if (password !== confirmPassword) {
      alert('Passwords do not match.');
      return;
    }

    console.log('New Password:', password);
    // Add logic to handle password change here
  };

  return (
    <div className="flex h-screen">
      <div className="relative w-4/6">
        <img src="background.jpg" alt="Background" className="w-full h-full object-cover" />
        <div className="absolute top-0 left-0 w-full h-full bg-black bg-opacity-50 flex flex-col justify-center items-center">
          <h1 className="text-6xl text-teal-400">Logo</h1>
          <h2 className="text-3xl my-4 text-white">Nama Website</h2>
          <p className="text-lg text-center text-white w-3/4">Jelajahi ribuan footage menarik di website kami!</p>
        </div>
      </div>
      <div className="w-2/6 bg-gray-800 flex flex-col justify-center items-center p-10">
        <h1 className="text-2xl mb-8 text-white">Pulihkan Akun Anda</h1>
        <p className="text-lg mb-6 text-white">Silahkan Masukkan Password Baru Anda</p>
        <form onSubmit={handleSubmit} className="w-full">
          <label htmlFor="password" className="block text-lg mb-2 text-white">Masukkan Password</label>
          <div className="relative mb-6">
            <input
              type={passwordVisible ? 'text' : 'password'}
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Masukkan password"
              className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            <i
              className={`fas ${passwordVisible ? 'fa-eye-slash' : 'fa-eye'} absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-600 cursor-pointer`}
              onClick={togglePasswordVisibility}
            ></i>
          </div>

          <label htmlFor="confirm-password" className="block text-lg mb-2 text-white">Masukkan Kembali Password</label>
          <div className="relative mb-6">
            <input
              type={passwordVisible ? 'text' : 'password'}
              id="confirm-password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              placeholder="Masukkan password"
              className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            <i
              className={`fas ${passwordVisible ? 'fa-eye-slash' : 'fa-eye'} absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-600 cursor-pointer`}
              onClick={togglePasswordVisibility}
            ></i>
          </div>

          <button
            type="submit"
            className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded-lg w-full"
          ><a href="AkunBerhasilDipulihkan.jsx">
            Ganti Password</a>
          </button>
        </form>
      </div>
    </div>
  );
};

export default PulihkanAkun;
