import React, { useState } from 'react';

const AkunBerhasilDipulihkan = () => {
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [passwordVisible, setPasswordVisible] = useState(false);

  const handleTogglePassword = () => {
    setPasswordVisible(!passwordVisible);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (password === "" || confirmPassword === "") {
      alert("Please fill in all fields.");
      return;
    }

    if (password !== confirmPassword) {
      alert("Passwords do not match.");
      return;
    }

    console.log("New Password: " + password);
    // Handle password reset logic here
  };

  return (

    
    <div className="flex h-screen">
      <div className="relative w-3/5">
        <img
          src="background.jpg"
          alt="Background"
          className="w-full h-full object-cover"
        />
        <div className="absolute inset-0 bg-black bg-opacity-50 flex flex-col justify-center items-center">
          <h1 className="text-6xl text-teal-400">Logo</h1>
          <h2 className="text-4xl mt-4">Nama Website</h2>
          <p className="text-xl text-center mt-2 w-3/4">
            Jelajahi ribuan footage menarik di website kami!
          </p>
        </div>
      </div>
      <div className="w-2/5 bg-gray-800 flex flex-col justify-center items-center p-10">
        <h1 className="text-3xl mb-6">Akun berhasil dipulihkan</h1>
        <p className="text-lg mb-8">Silahkan Masukkan Password Baru Anda</p>
        <form onSubmit={handleSubmit} className="w-full">
          <label htmlFor="password" className="block text-lg mb-2">
            Masukkan Password
          </label>
          <div className="relative mb-6">
            <input
              type={passwordVisible ? "text" : "password"}
              id="password"
              name="password"
              placeholder="Masukkan password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full p-3 rounded-lg border border-gray-500 bg-white text-black"
            />
            <i
              className={`fas ${
                passwordVisible ? "fa-eye-slash" : "fa-eye"
              } absolute right-3 top-3 cursor-pointer`}
              onClick={handleTogglePassword}
            ></i>
          </div>

          <label htmlFor="confirmPassword" className="block text-lg mb-2">
            Masukkan Kembali Password
          </label>
          <div className="relative mb-6">
            <input
              type={passwordVisible ? "text" : "password"}
              id="confirmPassword"
              name="confirmPassword"
              placeholder="Masukkan password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              className="w-full p-3 rounded-lg border border-gray-500 bg-white text-black"
            />
            <i
              className={`fas ${
                passwordVisible ? "fa-eye-slash" : "fa-eye"
              } absolute right-3 top-3 cursor-pointer`}
              onClick={handleTogglePassword}
            ></i>
          </div>

          <button
            type="submit"
            className="w-full bg-blue-500 text-white font-bold py-3 rounded-lg mt-4"
          ><a href="LoginPage.jsx">
            Ganti Password</a>
          </button>
        </form>
      </div>
    </div>
  );
};

export default AkunBerhasilDipulihkan;
